    this.state={
      score: 0
    }
  }

  updateScore = (arg) => {
    let num = 1

    if (this.state.sc)
    this.setState({
      score: this.state.score + num
    })
  }

  render (){
    return (
      <div>
        <p>Lutrivia</p>
        <Header />
        <QuestionsContainer updateScore={this.updateScore} currentScore={this.state.score}/>
        <ScoreForm />
      </div>
    );
  }
}
