import React from 'react';
import Header from './Header';
import QuestionsContainer from './QuestionsContainer'

class App extends React.Component {

  constructor(){
    super()
    this.state={
      score: 0
    }
  }

  updateScore = (num) => {
    this.setState({
      score: this.state.score + num
    })
  }

  render (){
    return (
      <div>
        <p>Lutrivia</p>
        <Header />
        <QuestionsContainer updateScore={this.updateScore}/>
      </div>
    );
  }
}
export default App;
