import React from 'react';

export default class Question extends React.Component{

    constructor(){
        super()
        this.state={
            question: ''
        }
    }

    componentDidMount = () => {
        this.setState({
            question: this.props
        })
    }


    handleClick = () => {
        this.setState({
            answer: !this.state.answer
        })
    }

    colors

    render(){
        return(
            <div>
                <h3>{this.props.text}</h3>
                <button style={this.state.answer ? color='red' : color='none'}>True</button>
                <button>False</button>
            </div>
        )
    }
}
