import React from 'react';

export default class Question extends React.Component{

    constructor(){
        super()
        this.state={
            answer: ''
        }
    }

    componentDidMount = () => {
        this.setState({
            answer: this.props.answer
        })
    }


    handleClick = (event) => {
        console.log(event.target.style.background)
        this.setState({
            answer: !this.state.answer
        })
    }

    render(){
        return(
            <div>
                <h3>{this.props.text}</h3>
                <button onClick={this.handleClick} style={this.state.answer ? {background: 'red' }: {background: 'none'}}>True</button>
                <button onClick={this.handleClick} style={!this.state.answer ? {background: 'blue' }: {background: 'none'}}>False</button>
            </div>
        )
    }
}
